INSERT INTO `card_notes` (`id`, `deck_id`, `message`, `color`, `font_size`) VALUES
(1, 501004, 'Hayal kur, gerçeğe bir adım daha yaklaş..', '#394a4f', '14px'),
(2, 501004, 'Kaygılarımdan özgürleşmeye niyet ediyor, kalbimi sevgiye açıyorum.', '#394a4f', '14px'),
(3, 501004, 'En büyük korkularımızdan biri, zengin olmak..', '#394a4f', '14px'),
(4, 501004, 'Bugün kendim için ne yaptım? Kendimi ve ihtiyaçlarımı fark etmeye niyet ediyorum.', '#394a4f', '14px'),
(5, 501004, 'Öfke duyduğum tüm kişileri ve durumları affetmeyi seçiyorum. Affettikçe özgürleşiyor ve hafifliyorum.', '#394a4f', '14px'),
(6, 501004, 'Varlığımı onaylıyorum. Değerliyim ve değer görmeyi hak ediyorum.', '#394a4f', '14px'),
(7, 501004, 'Hata nedir? Senin için iyi bir şey mi, kötü bir şey mi? Peki hatasız bir hayat olsa bunca öğrenimi nasıl elde ederdin?', '#394a4f', '14px'),
(8, 501004, 'Çocukluk hayalini hatırla!', '#394a4f', '14px'),
(9, 501004, 'Rotası olmayan bir gemiye hiçbir rüzgar fayda vermez.', '#394a4f', '14px'),
(10, 501004, 'Yaptığın işten keyif almak için..', '#394a4f', '14px'),
(11, 501004, 'Girişimci ruhunuzu sevin ve onunla yol almaya çalışın.', '#394a4f', '14px'),
(12, 501004, 'Artık sorumluluğu paylaştırın!', '#394a4f', '14px'),
(13, 501004, 'Başarıya koşuyorum ve kendimi başarılı buluyorum.', '#394a4f', '14px'),
(14, 501004, 'Sahip olduklarımı minnetle kucaklıyorum. Şükranlarımı sundukça bolluk ve bereketin bana doğru geldiğini fark ediyorum.', '#394a4f', '14px'),
(15, 501004, 'Kalbimin sevgi kapısını yeniden açıyorum.', '#394a4f', '14px'),
(16, 501004, 'Hayatımda var olan güzellikleri sevgiyle paylaşıyorum. Paylaştıkça çoğaldığını görüyorum.', '#394a4f', '14px'),
(17, 501004, 'Hayatı akışına bırakıp, sorumluluklarımı yerine getirirken, anda kalmak için kendime zaman ayırıyorum, kısa bir mola veriyorum.', '#394a4f', '14px'),
(18, 501004, 'Yaptığın yardım acaba kimin duasına icabet oluyor?', '#394a4f', '14px'),
(19, 501004, 'Hislerime güveniyorum, iç sesim bana yol gösteriyor. Sezgilerimle ilerliyorum.', '#394a4f', '14px'),
(20, 501004, 'Tam ve bütün olun!', '#394a4f', '14px'),
(21, 501004, 'Doğru frekansı yaydığımızı nasıl anlarız?', '#394a4f', '14px'),
(22, 501004, 'Üretken olmak sana iyi gelecek.', '#394a4f', '14px'),
(23, 501004, 'Şifalandırma Çalışması', '#394a4f', '14px'),
(24, 501004, 'Yeni düşüncelere yer açın', '#394a4f', '14px'),
(25, 501004, 'Enerji bağlarınızı doğru kurun!', '#394a4f', '14px'),
(26, 501004, 'Seçimler bir vazgeçiş, aynı zamanda bir başlangıçtır.', '#394a4f', '14px'),
(27, 501004, 'Yeni güne pozitif ve enerji dolu başlamaya niyet ediyorum.', '#394a4f', '14px'),
(28, 501004, 'İmkansızlık sadece bir düşünce gücüdür...', '#394a4f', '14px'),
(29, 501004, 'Senin için zaman değerli mi? Peki o kadar değerli zaman için neden hayır demeyi hala başaramıyorsun?', '#394a4f', '14px'),
(30, 501004, 'Bolluğu hayatınıza davet edin', '#394a4f', '14px'),
(31, 501004, 'Sonraki nesillere eser bırakmak ister misin?', '#394a4f', '14px'),
(32, 501004, 'Sırrı çözmek', '#394a4f', '14px'),
(33, 501004, 'Barış içinde olmak', '#394a4f', '14px'),
(34, 501004, 'Her iddia sınanır!', '#394a4f', '14px'),
(35, 501004, 'Fırsatı yakalamak', '#394a4f', '14px'),
(36, 501004, 'Esnek Olun!', '#394a4f', '14px'),
(37, 501004, 'Kafandaki konuya, daha fazla kafanı yormayı bırak.', '#394a4f', '14px'),
(38, 501004, 'Hayatta şekil vereceğin en önemli eserin kendinsin.', '#394a4f', '14px'),
(39, 501004, 'Geçmiş veya geleceği düşünmeden anı yaşamayı seçiyorum, anda kalıp tadını çıkarıyorum...', '#394a4f', '14px'),
(40, 501004, 'Tatlı tatlı adım atın.', '#394a4f', '14px'),
(41, 501004, 'Herkesin performansı aynı olmayabilir', '#394a4f', '14px'),
(42, 501004, 'Etrafınızdaki kişilerin performansını keşfedin', '#394a4f', '14px'),
(43, 501004, 'Ayağa kalkma vakti', '#394a4f', '14px'),
(44, 501004, 'Hayatınıza arızasız insanları nasıl çekersiniz?', '#394a4f', '14px'),
(45, 501004, 'Korktuğun şeyler başına mı geliyor?', '#394a4f', '14px'),
(46, 501004, 'Harika bir nefes çalışması', '#394a4f', '14px'),
(47, 501004, 'Birilerini küçük görmek, seni küçük düşürmez mi?', '#394a4f', '14px'),
(48, 501005, 'Söylenmeyi hemen şimdi bırak ve harekete geç', '#4e493f', '14px'),
(49, 501005, 'Kendini Ertelemekten Vazgeç', '#4e493f', '14px'),
(50, 501005, 'Yapabileceğine inanırsan yaparsın', '#4e493f', '14px'),
(51, 501005, 'Bugün seni motive edecek fırsatları fark et', '#4e493f', '14px'),
(52, 501005, 'Yeteneklerimi fark edebilmeye niyet ediyorum', '#4e493f', '14px'),
(53, 501005, 'Bedenine iyi bak', '#4e493f', '14px'),
(54, 501005, 'Doğanın içinde ol', '#4e493f', '14px'),
(55, 501005, 'Hadi biraz gülümse', '#4e493f', '14px'),
